mapper.select:
SELECT app_info.id AS id, softwareName, APKName,softwareSize, d1.valueName AS flatformName, a1.categoryName AS categoryLevel1Name, a2.categoryName AS categoryLevel2Name, a3.categoryName AS categoryLevel3Name, d2.valueName AS statusName, downloads,versionNo,versionId,status FROM app_info INNER JOIN data_dictionary AS d1 ON d1.valueId=app_info.flatformId AND d1.typeCode='APP_FLATFORM' INNER JOIN data_dictionary AS d2 ON d2.valueId = STATUS AND d2.typeCode = 'APP_STATUS' INNER JOIN app_category AS a1 ON a1.id=app_info.categoryLevel1 INNER JOIN app_category AS a2 ON a2.id=app_info.categoryLevel2 INNER JOIN app_category AS a3 ON a3.id=app_info.categoryLevel3 INNER JOIN app_version ON app_version.id=app_info.versionId
and softwareName=#{softwareName}
and flatformId=#{flatformId}
and categoryLevel1=#{categoryLevel1}
and categoryLevel2=#{categoryLevel2}
and categoryLevel3=#{categoryLevel3}
limit #{index},#{pageCount}

SELECT count(1) FROM app_info
INNER JOIN data_dictionary AS d1 ON d1.valueId=app_info.flatformId AND d1.typeCode='APP_FLATFORM'
INNER JOIN appinfodb.data_dictionary AS d2 ON d2.valueId = STATUS AND d2.typeCode = 'APP_STATUS'
INNER JOIN  app_category AS a1 ON a1.id=app_info.categoryLevel1
INNER JOIN  app_category AS a2 ON a2.id=app_info.categoryLevel2
INNER JOIN  app_category AS a3 ON a3.id=app_info.categoryLevel3
INNER JOIN app_version ON app_version.id=app_info.versionId


			and	softwareName=#{softwareName}


			and flatformId=#{flatformId}


			and categoryLevel1=#{categoryLevel1}


			and categoryLevel2=#{categoryLevel2}


			and categoryLevel3=#{categoryLevel3}

SELECT appinfodb.app_info.id AS id, softwareName,APKName,softwareSize,status, d1.valueName AS flatformName,versionId, d2.valueName AS statusName,downloads, a1.categoryName AS categoryLevel1Name, a2.categoryName AS categoryLevel2Name, a3.categoryName AS categoryLevel3Name, versionId AS versionNo FROM appinfodb.app_info INNER JOIN appinfodb.data_dictionary AS d1 ON d1.valueId = flatformId AND d1.typeCode = 'APP_FLATFORM' INNER JOIN appinfodb.data_dictionary AS d2 ON d2.valueId = status AND d2.typeCode= 'APP_STATUS' INNER JOIN appinfodb.app_category AS a1 ON categoryLevel1 = a1.id INNER JOIN appinfodb.app_category AS a2 ON categoryLevel2 = a2.id INNER JOIN appinfodb.app_category AS a3 ON categoryLevel3 = a3.id
and softwareName LIKE CONCAT('%',#{softwareName},'%')
AND flatformId=#{flatformId}
AND status=#{status}
AND categoryLevel1=#{categoryLevel1}
AND categoryLevel2=#{categoryLevel2}
AND categoryLevel3=#{categoryLevel3}
limit #{index},#{pageCount}

SELECT count(1) FROM app_info


		AND status=#{status}


		and softwareName LIKE CONCAT('%',#{softwareName},'%')


		and flatformId=#{flatformId}


		and categoryLevel1=#{categoryLevel1}


		and categoryLevel2=#{categoryLevel2}


		and categoryLevel3=#{categoryLevel3}
